$(document).ready(function () {
    $(".owl-wrapper-outer").owlCarousel({
        items: 1, // Nombre d'éléments à afficher à la fois
        loop: true, // Activer la lecture en boucle
        autoplay: true, // Activer la lecture automatique
        autoplayTimeout: 3000, // Délai entre chaque diapositive en millisecondes
        autoplayHoverPause: true // Mettre en pause la lecture automatique au survol
    });
});
